package sample;

public class Clue {

    private String clue;
    private int label;

    public Clue() {
        // TODO Auto-generated constructor stub
    }

    public Clue(String clue, int label) {
        this.clue = clue;
        this.label = label;
    }

    public int getLabel() {
        return label;
    }

    public String getClue() {
        return clue;
    }

}
